class UserManager {
    constructor() {
        this.users = [];
    }

    addUser(u) {
        this.users.push(u);
    }

    getActiveUsers() {
        var result = [];
        for (var i = 0; i < this.users.length; i++) {
            if (this.users[i].active == true) {
                result.push(this.users[i]);
            }
        }
        return result;
    }
}
